INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son YSL The Slim Glow Matte – 214 Illicit Orange', N'son-ysl-slim-214-illicit-orange.jpg', 890000, 10, 0, N'YSL tiếp tuc tung ra một siêu phẩm son nhỏ gọn mang tên Son YSL The Slim Glow Matte – 214 Illicit Orange. Từ khi ra mắt đến bây giờ cây  son nhỏ nhưng có võ của nhà YSL chưa bao giờ là hết sốt với các chị em.', null, 1, 4, 2);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Kem YSL Vinyl Cream Lip Stain 441 Arcade Chili', N'son-kem-ysl-vinyl-cream-lip-stain-441-arcade-chili.png', 1250000, 10, 0, N'Son Kem YSL Vinyl Cream Lip Stain 441 Arcade Chili – Màu Đỏ Cam Gạch là thỏi son được các chị em săn đón nhiều nhất trong thời gian gần đây. Thuộc thương hiệu nổi tiếng nên YSL 441 sở hữu chất son kem mềm mịn cùng độ giữ màu lên tới 10 giờ đồng hồ đã khiến chị em đứng ngồi không yên ngay từ khi mới ra mắt.', null, 2, 4, 3);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Kem YSL Vinyl Cream Lip Stain 610 Nude Champion', N'son-kem-ysl-vinyl-cream-lip-stain-610-nude-champion.png', 1250000, 12, 0, N'Son Kem YSL Vinyl Cream Lip Stain 610 Nude Champion – Màu Cam Nude gây ấn tượng với phái đẹp bởi màu son nhẹ nhàng, tự nhiên nhưng vẫn vô cùng cuốn hút và quyến rũ. Đặc biệt chất son kem mềm mịn, giữ màu lên tới 10 giờ đã làm chị em phải nhanh tay đặt hàng ngay từ khi mới ra mắt.', null, 2, 4, 9);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son YSL The Bold 07 Unihibited Flame', N'son-ysl-the-bold-07-unihibited-flame.png', 950000, 10, 0, N'Son YSL The Bold 07 Unihibited Flame – Màu Đỏ Tươi Ánh Cam là một trong thỏi son cao cấp nhà YSL được ưa chuộng bởi màu son tươi sáng, trẻ trung nhưng cũng không kém phần sang trọng và quyến rũ.', null, 1, 4, 6);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son YSL Rouge Volupte Shine Collector', N'son-ysl-rouge-volupte-shine-collector.jpg', 950000, 10, 0, N'Tone đỏ cherry nóng bỏng kết hợp cùng chất son satin cực mịn được “bao bọc” bởi lớp vỏ “nữ quyền” giúp cho Son YSL Rouge Volupte Shine Collector Màu Take My Red Away 120 khiến cho chị em không thể ngồi yên.', null, 1, 4, 5);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son YSL Rouge Volupte Candy Glaze 9 Tangerine Tease', N'son-ysl-rouge-volupte-candy-glaze-9-tangerine-tease.jpg', 890000, 10, 0, N'Màu son lên môi nhẹ nhàng, tự nhiên, vẫn chất son “nguyên bản” siêu cấp ẩm với độ bóng nhẹ trên làn môi, Son YSL Rouge Volupte Candy Glaze 9 Tangerine Tease – Màu Đỏ Tươi Ánh Cam lên môi một cách tự nhiên và quyến rũ lạ kỳ.', null, 1, 4, 6);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Allure Luminous Intense 212 Caractère', N'son-chanel-rough-allure-luminous-intense-212-caractete.png', 1050000, 20, 0, N'Son Chanel Rouge Allure Luminous Intense 212 Caractère – Màu Đỏ Nâu với tông màu cực trendy thời thượng chắc chắn sẽ không làm chị em thất vọng. Sở hữu thiết kế xinh đẹp, màu son thời thượng cùng chất son hoàn hảo, Chanel 212 Caractère dễ dàng trở thành gương mặt ưu tú trong những thỏi son được ưu chuộng.', null, 1, 1, 7);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Coco Baume 916 Flirty Coral', N'son-chanel-rouge-coco-baume-916-flirty-coral.jpg', 1450000, 15, 5, N'Son Chanel Rouge Coco Baume 916 Flirty Coral – Màu Cam Đào là một sản phẩm cao cấp của thương hiệu Chanel, sở hữu chức năng kép khi vừa lên màu cam đào siêu xinh vừa dưỡng môi hoàn hảo với khả năng giữ ẩm lên tới 12 tiếng. Đây thực sự là thỏi son siêu phẩm khiến bao nàng phải “chết mê chết mệt” ngay khi vừa ra mắt.', null, 1, 1, 8);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Coco Flash Hydrating Vibrant Shine Lip Colour 116 Easy', N'son-chanel-rouge-coco-flash-hydrating-vibrant-shine-lip-colour-116-easy.jpg', 1050000, 10, 2, N'Son Chanel Rouge Coco Flash Hydrating Vibrant Shine Lip Colour 116 Easy – Màu Cam Nude là thỏi son cao cấp của thương hiệu Chanel nổi tiếng nước Pháp. Rouge Allure không chỉ làm nổi bật vẻ thanh lịch và quyến rũ trên đôi môi phụ nữ mà còn đại diện cho sự thời thượng và gợi cảm không thể cưỡng. Với chất son lì mịn, lâu trôi và màu cam nude tự nhiên, dòng son này thực sự là “bùa yêu” quyến rũ của thế giới làm đẹp.', null, 1, 1, 9);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Allure Velvet 45 Intense', N'son-chanel-rouge-allure-velvet-45-intense-mau-hong-baby.jpg', 1050000, 12, 4, N'Son Chanel Rouge Allure Velvet 45 Intense – Màu Hồng Baby là thỏi son cao cấp đến từ thương hiệu Chanel nổi tiếng. Son sở hữu tông màu hồng baby ngọt ngào cũng không kém phần tự nhiên được nhiều cô nàng yêu thích.', null, 1, 1, 10);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Allure Velvet 43 La Favorite', N'son-chanel-rouge-allure-velvet-43-la-favorite-mau-hong-cam.jpg', 1050000, 10, 1, N'Son Chanel Rouge Allure Velvet 43 La Favorite – Màu Hồng Cam với tông màu hồng ánh cam tạo nên vẻ đẹp quyến rũ rạng ngời', N'', 1, 1, 11);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Allure Luminous Intense 152 Insaisissable', N'son-chanel-rouge-allure-luminous-intense-152-insaisissable-mau-hong-cam.jpg', 1000000, 5, 0, N'Son Chanel Rouge Allure Luminous Intense 152 Insaisissable – Màu Hồng Cam nhẹ nhàng hút hồn phái đẹp. Sắc hồng cam nhẹ nhàng thanh thoát của son Chanel 152 đẹp rạng ngời mà rất tôn da, từ tôn da trung bình đến da trắng đều có thể sử dụng được các nàng nhé.', null, 1, 1, 11);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Coco Baume 930 Sweet Treat', N'son-chanel-rouge-coco-baume-930-sweet-treat.jpg', 1450000, 10, 1, N'Son Chanel Rouge Coco Baume 930 Sweet Treat – Màu Hồng Đất là thỏi son sở hữu chất son dưỡng mềm mịn, nhẹ tênh như những cánh hoa xinh đẹp. Cuốn hút hơn nữa với màu hồng đất siêu Tây giúp nàng cân mọi outfit mỗi khi ra đường.', null, 1, 1, 12);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Coco Flash Hydrating Vibrant Shine Lip Colour 162 Sunbeam', N'son-chanel-rouge-coco-flash-hydrating-vibrant-shine-lip-colour-162-sunbeam.jpg', 1050000, 5, 0, N'Son Chanel Rouge Coco Flash Hydrating Vibrant Shine Lip Colour 162 Sunbeam – Màu Cam Đào với thiết kế thỏi son sang trọng, chất son mềm mượt lướt nhẹ trên môi, dưỡng ẩm tối ưu cho đôi môi căng mọng như quả anh đào. Em son này chắc chắn sẽ là item cân được cả layout trang điểm chanh sả hàng ngày.', null, 3, 1, 8);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Coco Flash Hydrating Vibrant Shine Lip Colour 148 Lively', N'son-chanel-rouge-coco-flash-hydrating-vibrant-shine-lip-colour.jpg', 1050000, 8, 1, N'Son Chanel Rouge Coco Flash Hydrating Vibrant Shine Lip Colour 148 Lively – Màu Đỏ Cherry ngọt ngào sang chảnh và cũng thật quyến rũ. Cho dù nàng đang theo đuổi phong cách nào thì em  Chanel 148 Lively này cũng dễ dàng giúp bạn cộng điểm trong mắt đối phương.', N'', 3, 1, 5);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Kem Dior Rouge Forever Liquid 861 Forever Charm', N'son-kem-dior-rouge-forever-liquid-861-forever-charm-mau-do-cherry.png', 890000, 20, 8, N'Son Kem Dior Rouge Forever Liquid 861 Forever Charm – Màu Đỏ Cherry ngọt ngào, sang chảnh và cũng thật quyến rũ. Cho dù nàng đang theo đuổi phong cách nào thi em Dior 861 này cũng dễ dàng giúp bạn cộng điểm trong mắt đối phương.', null, 2, 2, 5);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Dưỡng Dior Addict Lip Maximizer Collagen 020', N'son-duong-dior-addict-lip-maximizer-collagen-020-mau-do-nau.jpg', 850000, 5, 1, N'Son Dưỡng Dior Addict Lip Maximizer Collagen 020 – Màu Đỏ Nâu là cây son dưỡng nức tiếng của dòng son dưỡng cao cấp Dior. Dior Addict Lip Maximizer Collagen 020 với không chỉ chăm sóc bờ môi toàn diện mà còn có sắc son đỏ nâu siêu xinh. Dù là son dưỡng đi nữa thì em nó vẫn được nhiều chị em yêu thích và sử dụng như một món trang sức.', null, 3, 2, 7);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Dưỡng Dior Addict Lip Maximizer 009 Intense Rosewood', N'son-duong-dior-addict-lip-maximizer-009-intense-rosewood.jpg', 650000, 10, 1, N'Son Dưỡng Dior Addict Lip Maximizer 009 Intense Rosewood – Màu Hồng Đất là thỏi son dưỡng cao cấp của thương hiệu Dior sở hữu chức năng kép vừa làm đẹp cho đôi môi với màu hồng đất quyến rũ', N'', 3, 2, 12);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Dior Addict Lip Tint 561 Natural Poppy', N'son-dior-addict-lip-tint-561-natural-poppy.jpg', 920000, 7, 3, N'Son Dior Addict Lip Tint 561 Natural Poppy – Màu Đỏ Cam như ánh hoàng hôn rực rỡ, là sự hòa quyện tinh tế giữa sắc đỏ và sắc cam, làm nổi bật đôi môi quyến rũ. Vẻ đẹp nổi bật và nồng nàn của màu son này tạo cảm giác tự tin và cuốn hút, làm nổi bật sự mạnh mẽ và cá tính trong phong cách của phụ nữ hiện đại.', null, 2, 2, 6);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son YSL Rouge Pur Couture The Slim 37 Red On Fire', N'son-ysl-rouge-pur-couture-the-slim-37-red-on-fire.png', 950000, 10, 2, N'Đã là tín đồ mê làm đẹp thì không thể bỏ qua thỏi Son YSL Rouge Pur Couture The Slim 37 Red On Fire – Màu Cam Cháy với gam màu vô cùng ấn tượng này. Đặc biệt hơn, thỏi son này còn sở hữu chất son đẹp không có điểm nào để chê khiến bao nàng phải nhanh tay đặt hàng từ khi mới ra mắt.', null, 1, 4, 2);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Dior Rouge Forever Transfer Proof Lipstick 416 Forever Wild (New)', N'son-dior-rouge-forever-transfer-proof-lipstick-416-forever-wild.jpg', 950000, 5, 1, N'Son Dior Rouge Forever Transfer Proof Lipstick 416 Forever Wild – Màu Cam Cháy là màu son kinh điển của nhà Dior rất được ưa chuộng trong thời gian vừa qua. Son màu cam cháy mang đến vẻ đẹp nữ tính mà lại không kém phần quyến rũ và sang trọng.', null, 1, 2, 2);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Dior Ultra Rouge 707 Vỏ Đỏ', N'son-dior.jpg', 890000, 8, 2, N'Được xem là màu son đỉnh cao dành riêng cho mùa thu, “cam cháy” luôn có sức hút đặc biệt với các cô nàng ngọt ngào, Dior Ultra Rouge 707 vỏ đỏ sẽ mang đến cho bạn sắc son có 1 -0-2 này.', null, 1, 2, 2);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Dior Rouge Forever Transfer Proof Lipstick 866 Forever Together (New)', N'son-dior-rouge-forever-transfer-proof-lipstick.png', 950000, 10, 5, N'Son Dior Rouge Forever Transfer Proof Lipstick 866 Forever Together – Màu Đỏ Cherry ngọt ngào sang chảnh và cũng thật quyến rũ. Cho dù nàng đang theo đuổi phong cách nào thì em Dior 866 này cũng dễ dàng giúp bạn cộng điểm trong mắt đối phương.', N'', 1, 2, 5);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Dưỡng Dior Addict Lip Glow Dior 8', N'son-duong-dior-addict-lip-glow-dior-8-mau-do-dat.jpg', 850000, 8, 1, N'Những thỏi son màu đỏ đất luôn được chị em yêu thích và không ngạc nhiên khi Son Dưỡng Dior Addict Lip Glow Dior 8 – Màu Đỏ Đất vừa mới ra mắt đã trở nên cực kỳ hot. Sắc đỏ đất mang lại sự ấm áp và quyến rũ, đặc biệt, phiên bản son dưỡng Dior màu 08 pha thêm chút ngọt ngào đặc trưng riêng. Mặc dù không sặc sỡ như son màu, màu sắc nhẹ nhàng của son dưỡng vẫn đủ để làm bừng sáng vẻ ngoài và thu hút mọi ánh nhìn.', null, 3, 2, 13);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Dior Matte 884 Célèbre', N'Son-Dior-Matte-884-Celebre-mau-do-dat.png', 890000, 5, 2, N'Son Dior Matte 884 Célèbre – Màu Đỏ Đất tạo nên cơn sốt khiến chị em chao đảo với sắc đỏ quá xinh đẹp. Màu son đỏ đất khiến ai nấy đắm say ngây ngất, với chất son matte mịn màng, lướt nhẹ trên môi. Son giàu tinh chất từ hoa, chăm sóc đôi môi hiệu quả. Điểm nối bật mà phiên bản Rouge Dior này hướng đến là thân thiện với môi trường. Son có thể refill lõi, cực kỳ ấn tượng và tiện dụng.', null, 1, 2, 13);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Dior Rouge Matte 772 Classic', N'son-dior-rouge-matte-772-classic.jpg', 890000, 8, 4, N'Son Dior Rouge Matte 772 Classic – Màu Hồng Đất là thỏi son high-end cao cấp của thương hiệu nổi tiếng Dior. Sở hữu tông màu hồng pha chút nâu đất tạo nên một màu hồng đất nhẹ nhàng mà cô gái nào cũng yêu thích.', null, 1, 2, 12);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Dior Rouge Forever Transfer Proof Lipstick 625 Mitzah (New)', N'son-dior-rouge-forever-transfer-proof-lipstick-625-mitzah-new-mau-hong-dat.jpg', 950000, 5, 1, N'Những tín đồ của dòng son hồng không thể nào bỏ lỡ Son Dior Rouge Forever Transfer Proof Lipstick 625 Mitzah – Màu Hồng Đất đang hot gần đây. Màu son hồng đất vô cũng trendy lại dễ mix-match và biến hoá với các style ăn mặc phong cách chắc chắn sẽ khiến nàng hài lòng.', null, 1, 2, 12);
