INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Gucci Rouge À Lèvres Voile Mat 506 Louisa Red', N'son-gucci.jpg', 980000, 8, 3, N'Son Gucci Rouge À Lèvres Voile Mat Màu 506 Louisa Red Đỏ Cherry “ngọt ngào” ngập tràn khuyến rũ trên đôi môi các cô nàng. Thỏi son 506 tựa như một cây đũa thần bởi tông màu này có kết hợp với vô số phong cách trang điểm khác nhau.', null, 1, 3, 5);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Gucci Rouge À Lèvres Voile 25 Goldie Red (Vỏ Hoa)', N'son-gucci-rouge-a-levres-voile-25-goldie-red-vo-hoa.jpg', 1050000, 10, 5, N'Son Gucci Rouge À Lèvres Voile 25 Goldie Red – Màu Đỏ Tươi mang lại sự quyến rũ đê mê từ đôi môi. Cho dù nàng mê phong cách trang điểm nào thì em nó cũng có thể biến hóa. Không những vậy, thiết kế hoa nhẹ nhàng khiến thỏi son trở nên đặc biệt và thu hút như một món trang sức.', null, 1, 3, 14);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Gucci Rouge De Beauté Brillant 516 Margaret Ruby', N'son-gucci-rouge-de-beaute-brillant-516-margaret-ruby.png', 1050000, 8, 1, N'Son Gucci Rouge De Beauté Brillant 516 Margaret Ruby – Màu Đỏ Tươi Ánh Cam tone son được đánh giá là hot và bán chạy nhất bảng màu Gucci mới 2021 này. Thỏi son Gucci tinh tế trình làng với chiếc vỏ hoa hoàn toàn mới được lấy cảm hứng từ những chiếc trâm cài cổ điển mới lạ, làm “choáng ngợp”.', null, 1, 3, 6);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Gucci Rouge À Lèvres Mat 519 Pauline Red', N'son-gucci-rouge-a-levres-mat-519-pauline-red-mau-do-nau.jpg', 980000, 10, 1, N'Son Gucci Rouge À Lèvres Mat 519 Pauline Red – Màu Đỏ Nâu đến từ thương hiệu cao cấp Gucci mang một sức hút kì lạ khiến các nàng không khỏi mê man ngắm nhìn.', null, 1, 3, 7);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'on Gucci Rouge À Lèvres Mat Lipstick Limited 208 They Met In Argentina Xmas', N'son-gucci-rouge-a-levres-mat-lipstick-limited.jpg', 1050000, 5, 1, N'Son Gucci Rouge À Lèvres Mat Lipstick Limited 208 They Met In Argentina Xmas – Màu Hồng Đất là lựa chọn hoàn hảo của phái đẹp để diện trong dịp lễ giáng sinh và những ngày lễ hội cuối năm. Sở hữu màu hồng đất siêu trendy, Gucci 208 khi lên môi khiến nàng không khỏi ngỡ ngàng vì màu son đem tới sự trẻ trung và tươi mới như những cô gái tuổi teen trẻ đẹp và tràn đầy sức sống.', null, 1, 3, 12);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Gucci Rouge À Lèvres Mat Lipstick Limited 505 Janet Rust Xmas', N'son-gucci-rouge-a-levres-mat-lipstick-limited-505-janet-rust-xmas.jpg', 1050000, 5, 1, N'Son Gucci Rouge À Lèvres Mat Lipstick Limited 505 Janet Rust Xmas – Màu Đỏ Gạch là phiên bản giới hạn được thương hiệu Gucci cho ra mắt nhân dịp giáng sinh và mùa lễ hội cuối năm. Thỏi son này chiếm trọn trái tim chị em ngay ở phiên bản thường, nay có thêm phiên bản giới hạn với màu son đỏ gạch thời thượng cùng thiết kế đẹp chưa từng có của thương hiệu khiến phái đẹp mê ngay từ lần đầu tiên nhìn thấy.', null, 1, 3, 15);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Gucci Rouge À Lèvres Mat 308 Lucy Dark', N'son-gucci-rouge-a-levres-mat-308-lucy-dark-mau-cam-dat.jpg', 980000, 15, 6, N'Gucci trở lại ngoạn mục với một loạt những cây Son Gucci Rouge À Lèvres Mat 308 Lucy Dark – Màu Cam Đất đẹp rụng rời. Vẫn là lớp vỏ vàng lộng lẫy đầy kiêu hãnh chuẩn hàng hiệu, vẫn là chất lì mịn mượt đẳng cấp, nhưng dấu ấn đậm nét nhất của son Gucci Matte 308 nằm ở màu son trendy, không hề đụng hàng.', null, 1, 3, 16);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Dior Rouge Dior Satin Lipstick 419 Bois Rosé', N'son-dior-rouge-dior-satin-lipstick-419-bois-rose.png', 990000, 8, 0, N'Nếu nàng là một cô gái yêu thích sự trẻ trung, năng động mà vẫn phải có nét quyến rũ thì Son Dior Rouge Dior Satin Lipstick 419 Bois Rosé – Màu Cam Đất là thỏi son môi phù hợp nhất. Không chỉ sở hữu gam màu đẹp mắt mà còn có chất son mềm mịn cho đôi môi căng mọng như trái cam chín bọng nước khiến ai cũng phải ngỡ ngàng vì vẻ đẹp của đôi môi nàng.', null, 1, 2, 16);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Romand New Zero Matte Lipstick 21 Smoked Beige', N'son-romand-new-zero-matte-lipstick-21-moked-beige.jpg', 245000, 15, 9, N'Son Romand New Zero Matte Lipstick 21 Smoked Beige – Màu Cam Đất là sự hòa quyện tinh tế giữa sắc cam ấm áp và nâu đất trầm, tạo nên tông màu hiện đại, chất lừ, đầy quyến rũ làm nổi bật vẻ tự tin và quyến rũ của đôi môi.', null, 1, 4, 16);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son YSL Rouge Pur Couture Caring Satin Lipstick NM Nu Muse Limited Edition', N'son-ysl-rouge-pur-couture-caring-satin-lipstick-nm-nu-muse-limited-edition.jpg', 1050000, 8, 3, N'Son YSL Rouge Pur Couture Caring Satin Lipstick NM Nu Muse Limited Edition – Màu Cam Đất với thiết kế vỏ thỏi màu đỏ sang trọng, quý phái cùng sắc cam đất hiện đại, mang đến vẻ đẹp quyến rũ và thu hút. Cùng với chất son siêu mềm mịn và lưu màu lên đến 8 tiếng, đây đích thực là siêu phẩm mà chị em cần phải “bỏ túi” ngay.', null, 1, 4, 16);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Coco Flash Hydrating Vibrant Shine Lip Colour 174 Destination', N'son-chanel-rouge-coco-flash-hydrating-vibrant-shine-lip-colour-174-destination.jpg', 1050000, 9, 1, N'Son Chanel Rouge Coco Flash Hydrating Vibrant Shine Lip Colour 174 Destination – Màu Cam Đất là một thỏi son được đông đảo chị em yêu thích. Với gam màu cam đất tự nhiên, cho lên môi màu sắc ấm áp giúp các nàng trẻ trung tươi tắn mà không cần trang điểm quá cầu kỳ.', null, 1, 1, 16);
