INSERT INTO lipstick_store.dbo.brand (brand_name, brand_description) VALUES (N'Chanel', N'<p><span class="fw-medium">Thương hiệu Chanel</span> là một thương hiệu được thành lập từ những năm 1909 – 1910 do Gabrielle “Coco” Chanel sáng lập. Chanel là một thương hiệu thời trang, mỹ phẩm cao cấp hàng đầu nước Pháp, mang trọn vẹn những tinh hoa, nét đẹp của đất nước này.</p>
INSERT INTO lipstick_store.dbo.brand (brand_name, brand_description) VALUES (N'Dior', N'<p><span class="fw-medium">Dior</span> là một thương của Pháp thành lập vào năm 1946 bởi Christian Dior thuộc kiểm soát của LVMH cùng một công ty với Fendi. Ngoài mảng chủ đạo là thời thì hãng còn nổi tiếng về các dòng mỹ phẩm. Mỹ phẩm của Dior được biết đến với chất lượng đi đôi cùng với vẻ ngoài sang trọng, nhã nhặn.</p>
INSERT INTO lipstick_store.dbo.brand (brand_name, brand_description) VALUES (N'Gucci', N'<p><span class="fw-medium">Thương hiệu Gucci</span>  được thành lập từ năm 1921,Gucci là một thương hiệu thời trang Ý. Tiếp nối thành công với các sản phẩm thời trang, Gucci cho ra đời các sản phẩm mỹ phẩm cao cấp dành cho giới thượng lưu.</p>
INSERT INTO lipstick_store.dbo.brand (brand_name, brand_description) VALUES (N'YSL', N'<p><span class="fw-medium">YSL</span> là tên viết tắt của thương hiệu <span class="fw-medium">Yves Saint Laurent</span>, một thương hiệu về thời trang và mỹ phẩm cao cấp nổi tiếng đến từ Pháp, được thành lập vào năm 1961 bởi nhà thiết kế Yves Saint Laurent và Pierre Bergé.</p>
INSERT INTO lipstick_store.dbo.brand (brand_name, brand_description) VALUES (N'Louboutin', N'<p><span class="fw-medium">Son Christian Louboutin</span> là thỏi son đáng mơ ước của mọi cô gái, chúng được ví như những món trang sức thần kỳ “hô biến” nhan sắc của bạn trở nên lộng lẫy và kiêu sa; bởi vậy dù có mức giá khá đắt nhưng son Louboutin vẫn khiến các nàng phải mê mệt và mơ ước được sở hữu thỏi son này. </p>
INSERT INTO lipstick_store.dbo.brand (brand_name, brand_description) VALUES (N'MAC', N'<p><span class="fw-medium">Son MAC</span> là dòng son cao cấp, được các chuyên gia trang điểm nổi tiếng và các sao trên toàn thế giới tin dùng. Với đa đạng về màu sắc và kiểu cách son MAC thích hợp với nhiều đối tượng mang đến cho người dùng sự gợi cảm, sang trọng và quyến rũ giúp bạn nỗi bật dưới mọi ánh nhìn.</p>');
INSERT INTO lipstick_store.dbo.brand (brand_name, brand_description) VALUES (N'3CE', N'<p><span class="fw-medium">3CE</span> có tên gọi là Concept Eyes đến từ xứ kim chi, thành lập vào tháng 1/2009. Với hơn 10 năm sự nghiệp, <span class="fw-medium">3CE</span> đã nhanh chóng phổ biến trên thị trường với nhiều mặt hàng thời trang và mỹ phẩm. Trong đó mỹ phẩm <span class="fw-medium">3CE</span> luôn làm cho tín đồ làm đẹp trên Thế Giới “điên đảo”.</p>
INSERT INTO lipstick_store.dbo.brand (brand_name, brand_description) VALUES (N'Black Rouge', N'<p><span class="fw-medium">Black Rouge</span> là một thương hiệu ra đời năm 2016 và đến từ đất nước mặt trời mọc – Hàn Quốc – đất nước có vô số những thương hiệu mỹ phẩm uy tín và nổi tiếng với những cô gái có làn da căng mịn đáng ao ước. Nên mặc dù là một thương hiệu mỹ phẩm mới ra mắt những năm gần đây nhưng đã được giới làm đẹp tin tưởng lựa chọn bởi những thiết kế và sản phẩm vô cùng đa dạng và trendy.</p>
INSERT INTO lipstick_store.dbo.brand (brand_name, brand_description) VALUES (N'Romand', N'<p><span class="fw-medium">Romand</span> là thương hiệu Hàn Quốc luôn mang đến những sản phẩm trang điểm trẻ trung, tươi tắn, phù hợp với những ai yêu thích phong cách tự nhiên mà vẫn không kém phần nổi bật. Với <span class="fw-medium">Son Romand</span>, bạn có thể tìm thấy được màu sắc phù hợp, đồng thời trải nghiệm những sản phẩm chất lượng cao đến từ Hàn Quốc.</p>
INSERT INTO lipstick_store.dbo.brand (brand_name, brand_description) VALUES (N'Tom Ford', N'<p><span class="fw-medium">Son Tom Ford</span> là dòng sản phẩm high-end rất được lòng các chị em phụ nữ hiện nay. Sản phẩm này sở hữu bảng màu vô cùng đẹp mắt và quyến rũ, là niềm ao ước của rất nhiều người hiện nay.</p>
INSERT INTO category (category_name, category_description) VALUES (N'Son Thỏi', N'<p><span class="fw-medium">Son thỏi</span>, hay còn gọi là son sáp, thường có dạng cây dài rất dễ bảo quản và đa dạng về màu sắc cũng như loại son. Các loại son như dưỡng môi, son bóng hầu hết các loại son lì đều là <span class="fw-medium">son thỏi</span>. Sử dụng <span class="fw-medium">son thỏi</span> khá tiện lợi, vì chị em có thể bỏ túi và khi sử dụng có thể dùng trực tiếp thoa lên môi.</p>
INSERT INTO category (category_name, category_description) VALUES (N'Son Kem', N'<p><span class="fw-medium">Son kem</span> (hay còn gọi là creame lipstick) là một loại son có dạng sệt được đựng trong lọ và có cọ kèm theo như son tint (son nước). Tuy nhiên hiện nay trên thị trường cũng đã xuất hiện loại <span class="fw-medium">son kem</span> dạng thỏi thay cho loại lọ son truyền thống.</p>
INSERT INTO category (category_name, category_description) VALUES (N'Son Dưỡng', N'<p><span class="fw-medium">Son dưỡng</span> là một sản phẩm chăm sóc môi rất cần thiết, đây là một loại son chứa nhiều chất dưỡng, có tác dụng dưỡng ẩm, làm mềm môi, chống nứt nẻ, khô ráp, làm hồng môi...</p>
