INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son YSL Slim Velvet 21 Rouge Paradoxe', N'son-ysl-slim-velvet-21-rouge-paradox.png', 950000, 10, 0, N'Son YSL Slim Velvet 21 Rouge Paradoxe – Màu Đỏ Ruby ra đời đã khiến nhiều tín đồ mê làm đẹp nhanh tay đặt hàng bởi màu son đỏ ruby thời thượng cùng chất son lì với kết cấu mềm mại như nhung mịn màng khiến nàng thích thú.', null, 1, 4, 1);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son YSL The Slim Glow Matte – 214 Illicit Orange', N'son-ysl-slim-214-illicit-orange.jpg', 890000, 10, 0, N'YSL tiếp tuc tung ra một siêu phẩm son nhỏ gọn mang tên Son YSL The Slim Glow Matte – 214 Illicit Orange. Từ khi ra mắt đến bây giờ cây  son nhỏ nhưng có võ của nhà YSL chưa bao giờ là hết sốt với các chị em.', null, 1, 4, 2);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Kem YSL Vinyl Cream Lip Stain 441 Arcade Chili', N'son-kem-ysl-vinyl-cream-lip-stain-441-arcade-chili.png', 1250000, 10, 0, N'Son Kem YSL Vinyl Cream Lip Stain 441 Arcade Chili – Màu Đỏ Cam Gạch là thỏi son được các chị em săn đón nhiều nhất trong thời gian gần đây. Thuộc thương hiệu nổi tiếng nên YSL 441 sở hữu chất son kem mềm mịn cùng độ giữ màu lên tới 10 giờ đồng hồ đã khiến chị em đứng ngồi không yên ngay từ khi mới ra mắt.', null, 2, 4, 3);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Kem YSL Vinyl Cream Lip Stain 610 Nude Champion', N'son-kem-ysl-vinyl-cream-lip-stain-610-nude-champion.png', 1250000, 12, 0, N'Son Kem YSL Vinyl Cream Lip Stain 610 Nude Champion – Màu Cam Nude gây ấn tượng với phái đẹp bởi màu son nhẹ nhàng, tự nhiên nhưng vẫn vô cùng cuốn hút và quyến rũ. Đặc biệt chất son kem mềm mịn, giữ màu lên tới 10 giờ đã làm chị em phải nhanh tay đặt hàng ngay từ khi mới ra mắt.', null, 2, 4, 9);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son YSL The Bold 07 Unihibited Flame', N'son-ysl-the-bold-07-unihibited-flame.png', 950000, 10, 0, N'Son YSL The Bold 07 Unihibited Flame – Màu Đỏ Tươi Ánh Cam là một trong thỏi son cao cấp nhà YSL được ưa chuộng bởi màu son tươi sáng, trẻ trung nhưng cũng không kém phần sang trọng và quyến rũ.', null, 1, 4, 6);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son YSL Rouge Volupte Shine Collector', N'son-ysl-rouge-volupte-shine-collector.jpg', 950000, 10, 0, N'Tone đỏ cherry nóng bỏng kết hợp cùng chất son satin cực mịn được “bao bọc” bởi lớp vỏ “nữ quyền” giúp cho Son YSL Rouge Volupte Shine Collector Màu Take My Red Away 120 khiến cho chị em không thể ngồi yên.', null, 1, 4, 5);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son YSL Rouge Volupte Candy Glaze 9 Tangerine Tease', N'son-ysl-rouge-volupte-candy-glaze-9-tangerine-tease.jpg', 890000, 10, 0, N'Màu son lên môi nhẹ nhàng, tự nhiên, vẫn chất son “nguyên bản” siêu cấp ẩm với độ bóng nhẹ trên làn môi, Son YSL Rouge Volupte Candy Glaze 9 Tangerine Tease – Màu Đỏ Tươi Ánh Cam lên môi một cách tự nhiên và quyến rũ lạ kỳ.', null, 1, 4, 6);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Allure Luminous Intense 212 Caractère', N'son-chanel-rough-allure-luminous-intense-212-caractete.png', 1050000, 20, 0, N'Son Chanel Rouge Allure Luminous Intense 212 Caractère – Màu Đỏ Nâu với tông màu cực trendy thời thượng chắc chắn sẽ không làm chị em thất vọng. Sở hữu thiết kế xinh đẹp, màu son thời thượng cùng chất son hoàn hảo, Chanel 212 Caractère dễ dàng trở thành gương mặt ưu tú trong những thỏi son được ưu chuộng.', null, 1, 1, 7);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Coco Baume 916 Flirty Coral', N'son-chanel-rouge-coco-baume-916-flirty-coral.jpg', 1450000, 15, 5, N'Son Chanel Rouge Coco Baume 916 Flirty Coral – Màu Cam Đào là một sản phẩm cao cấp của thương hiệu Chanel, sở hữu chức năng kép khi vừa lên màu cam đào siêu xinh vừa dưỡng môi hoàn hảo với khả năng giữ ẩm lên tới 12 tiếng. Đây thực sự là thỏi son siêu phẩm khiến bao nàng phải “chết mê chết mệt” ngay khi vừa ra mắt.', null, 1, 1, 8);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Coco Flash Hydrating Vibrant Shine Lip Colour 116 Easy', N'son-chanel-rouge-coco-flash-hydrating-vibrant-shine-lip-colour-116-easy.jpg', 1050000, 10, 2, N'Son Chanel Rouge Coco Flash Hydrating Vibrant Shine Lip Colour 116 Easy – Màu Cam Nude là thỏi son cao cấp của thương hiệu Chanel nổi tiếng nước Pháp. Rouge Allure không chỉ làm nổi bật vẻ thanh lịch và quyến rũ trên đôi môi phụ nữ mà còn đại diện cho sự thời thượng và gợi cảm không thể cưỡng. Với chất son lì mịn, lâu trôi và màu cam nude tự nhiên, dòng son này thực sự là “bùa yêu” quyến rũ của thế giới làm đẹp.', null, 1, 1, 9);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Allure Velvet 45 Intense', N'son-chanel-rouge-allure-velvet-45-intense-mau-hong-baby.jpg', 1050000, 12, 4, N'Son Chanel Rouge Allure Velvet 45 Intense – Màu Hồng Baby là thỏi son cao cấp đến từ thương hiệu Chanel nổi tiếng. Son sở hữu tông màu hồng baby ngọt ngào cũng không kém phần tự nhiên được nhiều cô nàng yêu thích.', null, 1, 1, 10);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Chanel Rouge Allure Velvet 43 La Favorite', N'son-chanel-rouge-allure-velvet-43-la-favorite-mau-hong-cam.jpg', 1050000, 10, 1, N'Son Chanel Rouge Allure Velvet 43 La Favorite – Màu Hồng Cam với tông màu hồng ánh cam tạo nên vẻ đẹp quyến rũ rạng ngời', N'', 1, 1, 11);
