INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Kem MAC Locked Kiss Ink 24HR Lipcolour 95 Vicious', N'son-kem-mac-locked-kiss-ink-24hr-lipcolour-95-vicious.jpg', 699000, 10, 2, N'Son Kem MAC Locked Kiss Ink 24HR Lipcolour 95 Vicious – Màu Đỏ Cam là thỏi son cao cấp đến từ thương hiệu MAC nổi tiếng. Sở hữu một tone màu sống động kết hợp giữa đỏ và cam, khi lên màu môi cực chuẩn. Với sự pha trộn màu sắc hay ho, thú vị, son MAC màu đỏ cam này ghi điểm với bất cứ ai.', null, 2, 6, 3);
