INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Kem MAC Locked Kiss Ink 24HR Lipcolour 66 Terracotta', N'son-kem-mac-locked-kiss-ink-24hr-lipcolour-66-terracotta.jpg', 699000, 5, 5, N'Son Kem MAC Locked Kiss Ink 24HR Lipcolour 66 Terracotta – Màu Cam Cháy không chỉ thể hiện sự quyến rũ kết hợp một cách hoàn hảo với vẻ quyền lực. Đây không chỉ là một lớp son mà là biểu tượng của vẻ đẹp đầy cá tính và sức sống. Cùng đắm chìm vào thế giới đầy mê hoặc của dòng son kem đỉnh cao này nhé.', null, 2, 6, 2);
