INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son MAC Powder Kiss Velvet Blur Slim 894 Gingerella', N'son-mac-powder-kiss-velvet-blur-slim-894-gingerella-mau-cam-dao.jpg', 695000, 10, 1, N'Son MAC Powder Kiss Velvet Blur Slim 894 Gingerella – Màu Cam Đào với sắc cam đào dễ thương hoàn thiện vẻ đẹp cho gương mặt bởi sắc độ trẻ trung và hợp xu hướng. Chất son lì như nhung cùng thiết nhỏ gọn dễ dàng mang theo bên mình mọi lúc mọi nơi.', N'', 1, 6, 8);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son MAC Powder Kiss Velvet Blur Slim 889 Ruby New', N'son-mac-powder-kiss-velvet-blur-slim-889-ruby-new-mau-do-ruby.jpg', 695000, 10, 1, N'Son MAC Powder Kiss Velvet Blur Slim 889 Ruby New – Màu Đỏ Ruby là cây son lì cao cấp nổi bật với sắc đỏ quyến rũ cùng khả năng lên màu cực chuẩn và tôn da. Chất son mềm mượt bao phủ môi nhưng cho cảm giác nhẹ môi như chưa thoa gì.', N'', 1, 6, 1);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son MAC Powder Kiss Velvet Blur Slim 898 Sheer Outrage', N'son-mac-powder-kiss-velvet-blur-slim-898-sheer-outrage-mau-hong-dao.jpg', 695000, 20, 3, N'Một màu son mới gây ấn tượng ngay từ những cái chạm mắt đầu tiên chính là thỏi Son MAC Powder Kiss Velvet Blur Slim 898 Sheer Outrage – Màu Hồng Đào. Một sắc hồng nhẹ nhàng thanh lịch tạo lên vẻ đẹp sang trọng và dịu dàng. Đặc biệt, chất lì kiểu nhung mềm, bột mịn, một đàn em thừa hưởng tinh túy và đột phá phát triển để làm hài lòng tín đồ nhà MAC hơn.', null, 1, 6, 18);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son MAC Powder Kiss Velvet Blur Slim 895 Sorry Not Sorry', N'son-mac-powder-kiss-velvet-blur-slim-895-sorry-not-sorry-mau-cam-chay.jpg', 695000, 10, 1, N'Son MAC Powder Kiss Velvet Blur Slim 895 Sorry Not Sorry – Màu Cam Cháy phù hợp với làn da Châu Á cùng chất son mịn như bơ với công thức blur mới làm mở rãnh môi tạo nên một đôi môi nổi bất quyến rũ thành tâm điểm không thể bỏ qua.', N'', 1, 6, 2);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son MAC Powder Kiss Velvet Blur Slim 886 Marrakesh Mere', N'son-mac-powder-kiss-velvet-blur-slim-886-marrakesh-mere-mau-do-nau.jpg', 695000, 10, 4, N'Son MAC Powder Kiss Velvet Blur Slim 886 Marrakesh Mere – Màu Đỏ Nâu với sắc son thuộc top màu son thịnh hàng đang được săn đón. Màu Đỏ Nâu vừa trầm lắng, nhẹ nhàng nhưng cũng rất cá tính. Đặc biệt, chất son gây ấn tượng khi lên màu nổi bật chỉ một lần tô với chất son mịn như nhung.', null, 1, 6, 7);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son MAC Powder Kiss Velvet Blur Slim 875 Devoted To Danger', N'son-mac-powder-kiss-velvet-blur-slim-875-devoted-to-danger-mau-do-cam.jpg', 695000, 10, 1, N'Son MAC Powder Kiss Velvet Blur Slim 875 Devoted To Danger – Màu Đỏ Cam với sắc đỏ cam hoàn hảo khiến nàng luôn rạng rỡ và nổi bật. Sự cải tiến vượt bậc về công thức giúp chất son mịn màng như blur đôi môi, chuẩn xu hướng đôi môi mờ ảo trước đó của MAC.', null, 1, 6, 3);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son MAC Black Cherry Bloombox (Bản Giới Hạn)', N'son-mac-black-cherry-bloombox-ban-gioi-han-mau-cam-dao.jpg', 690000, 10, 0, N'MAC cho ra mắt bộ sưu tập mới mang tên Black Cherry tận hưởng trọn vẹn mùa đẹp nhất trong năm với những cánh hoa anh đào nở rộ. Son MAC Black Cherry Bloombox (Bản Giới Hạn) – Màu Cam Đào mang tông màu cam đào nhẹ nhàng cho bờ môi thêm điểm đáng yêu.', null, 1, 6, 8);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son MAC Powder Kiss Lipstick 935 Ruby New', N'son-mac-powder-kiss-lipstick-935-ruby-new.jpg', 540000, 10, 10, N'Son MAC Powder Kiss Lipstick 935 Ruby New – Màu Đỏ Thuần màu son mới nhất là sự kết hợp của màu son Mac huyền thoại Ruby Woo và chất son thế hệ mới Powder Kiss Lipstick. Đây là một sự kết hợp hoàn hảo tạo nên sự rực rỡ cho huyền thoại son MAC. Và đây cũng một trong những mới nhất hiện nay của MAC màu Ruby New đỏ thuần cực tôn da và thu hút.', null, 1, 6, 17);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Kem MAC Love Me Liquid Lipcolour – 486 Marrakesh Me Later', N'son-kem-mac-love-me-liquid-lipcolour-486-marrakesh-me-later.jpeg', 695000, 8, 3, N'Son Kem MAC Love Me Liquid Lipcolour – 486 Marrakesh Me Later mang đến tone đỏ đất  mới lạ cùng thiết kế hoàn toàn mới của nhà MAC đang khiến chị em săn lùng. Hãy cùng xem lần trở lại này MAC sẽ chinh phục giới làm đẹp ra sao nha.', N'', 2, 6, 13);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Kem Lì 3CE Hazy Lip Clay Mauvbrown', N'son-kem-li-3ce-hazy-lip-clay-mauvbrown.jpg', 390000, 20, 4, N'Son Kem Lì 3CE Hazy Lip Clay Mauvbrown – Màu Hồng Nude là sự pha trộn tinh tế giữa sắc hồng nhạt và màu nude tự nhiên, tạo ra gam màu nhẹ nhàng, thanh lịch, và trẻ trung. Sắc hồng nude tạo điểm nhấn nhẹ nhàng, vẻ tự nhiên và ấm áp. Tất cả điều này mang đến vẻ đẹp mềm mại và tinh tế, thể hiện sự thanh khiết và tươi trẻ.', null, 2, 7, 19);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Kem Lì 3CE Hazy Lip Clay Hipamine', N'son-kem-li-3ce-hazy-lip-clay-hipamine.jpg', 390000, 10, 6, N'Son Kem Lì 3CE Hazy Lip Clay Hipamine – Màu Đỏ Đất là sự hòa trộn độc đáo giữa đỏ tươi và nâu đất, tạo nên gam màu mới lạ và thời thượng, thu hút các tín đồ trang điểm. Làm say đắm bất kỳ ai yêu thích sự độc đáo của màu sắc. Đây là lý do vì sao màu son đỏ đất trở thành lựa chọn số 1 cho cô gái nào đang theo đuổi sự phong cách và cá tính.', null, 2, 7, 13);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Kem Lì 3CE Hazy Lip Clay Whip Red', N'son-kem-li-3ce-hazy-lip-clay-whip-red.jpg', 390000, 15, 13, N'Son Kem Lì 3CE Hazy Lip Clay Whip Red – Màu Đỏ Gạch, vừa tinh tế vừa quyến rũ. Màu sắc phản ánh sự mạnh mẽ, cá tính và hiện đại. Dù nổi bật nhưng vẫn giữ được sự ấm áp, phù hợp với mọi tông da và mọi phong cách trang điểm. Whip Red không chỉ là sự lựa chọn màu sắc, mà còn là biểu tượng cho sự tự tin và cuốn hút.', null, 2, 7, 15);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son 3CE Blur Matte Lipstick Peanut Beige', N'son-3ce-blur-matte-lipstick-peanut-beige.jpg', 415000, 10, 9, N'Son 3CE Blur Matte Lipstick Peanut Beige – Màu Cam Đất là một thỏi son chứa đựng nhiều thông điệp của thương hiệu 3CE khi tôn lên vẻ đẹp ấm áp, dịu dàng của các chị em.', null, 1, 7, 16);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son 3CE Blur Matte Lipstick In The Fog', N'son-3ce-blur-matte-lipstick-in-the-fog.jpg', 415000, 10, 1, N'Khó có thể biết liệu 3CE đã lấy cảm hứng màu sắc từ đâu mà có thể tạo ra lần này đến lần khác những màu son đặc biệt khiến chị em đứng ngồi không yên. Sắc Son 3CE Blur Matte Lipstick In The Fog – Màu Hồng Nude là sự kết hợp giữa gam màu hồng nhạt, tím và ánh nâu tạo nên vẻ đẹp hiền dịu, duyên dáng và đầy tinh khôi.', null, 1, 7, 19);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son 3CE SOFT MATTE LIPSTICK #MURMURING', N'son-3ce-soft-matte-lipstick-murmuring.jpeg', 395000, 16, 7, N'Màu hồng nude ngọt ngào và dịu dàng cùng chất son lì mịn sẽ là những gì mà Son 3CE SOFT MATTE LIPSTICK #MURMURING mang đến cho nàng, không chỉ vậy thiết kế ấn tượng cũng sẽ khiến nàng phải trầm trồ.', null, 1, 7, 19);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son 3CE Slim Velvet Lip Color Simple Stay', N'son-3ce-slim-velvet-lip-color-simple-stay.jpg', 395000, 10, 3, N'3CE Slim Velvet Lip Color Simple Stay là sắc cam nude nhưng được pha thêm tông đất để trở thành một sắc son ấm áp mà không kém phần trendy. Chính yếu tố này giúp Simple Stay không kén da như những màu nude thông thường, em ấy phù hợp với những cô nàng có làn da ngăm đến da trắng sáng.', null, 1, 7, 9);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son 3CE Slim Velvet Lip Color Vermilion', N'son-3ce-vermilion.jpg', 395000, 6, 5, N'3CE Slim Velvet Lip Color Vermilion là một màu đỏ cam vô cùng nổi bật và trẻ trung. Vermilion không chỉ mang lại cho bạn sự năng động mà vẫn có thể giúp nàng trở nên thật cuốn hút.', null, 1, 7, 3);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son 3CE Slim Velvet Lip Color True Red', N'son-3ce-slim-velvet-lip-color-true-red.jpg', 395000, 10, 1, N'Đỏ thuần được mệnh danh là màu sắc quốc dân vì phù hợp với mọi tone da và không kén bất kỳ style nào của nàng hết. Tuy nhiên, 3CE Slim Velvet Lip Color True Red lại là màu đỏ thuần thiên về tone lạnh nên mang lại sức hút đầy sang chảnh cho các cô nàng.', null, 1, 7, 17);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son 3CE Slim Velvet Lip Color Fluffy Red', N'son-3ce-slim-velvet-lip-color-fluffy-red.jpg', 395000, 10, 4, N'Fluffy Red là màu đỏ đất trầm vô cùng trendy. Màu son này phù hợp với mọi tone da của nàng từ làn da ngăm đến những cô gái sở hữu làn da trắng hồng. Không chỉ thế', N'', 1, 7, 13);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son 3CE Slim Velvet Lip Color Plain', N'son-3ce-slim-velvet-lip-color-plain.jpg', 395000, 10, 5, N'3CE Slim Velvet Lip Color Plain mang sắc đỏ nâu thuần túy nổi bật hơn hẳn so với các chị em trong bộ sưu tập. Sắc son này chắc chắn sẽ giúp cho làn da và men răng của bạn sáng hơn rất nhiều.', null, 1, 7, 7);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son 3CE Slim Velvet Lip Color Snow Glass', N'son-3ce-snow-glass.jpg', 395000, 10, 5, N'Snow Glass là một màu đỏ hồng ánh tím ngọt ngào, ấm áp. Màu sắc này rất thích hợp với những cô nàng nữ tính dùng hằng ngày và phù hợp với mọi tông da. Snow Glass sẽ giúp mọi cô gái trở nên hấp dẫn hơn bao giờ hết.', null, 1, 7, 12);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Tint Bóng Black Rouge Half N Half Water Glow HG04 Over Tension', N'son-tint-bong-black-rouge-half-water-glow-hg04-over-tension-do-gach.png', 220000, 10, 3, N'Son Tint Bóng Black Rouge Half N Half Water Glow HG04 Over Tension- Màu Đỏ Gạch cá tính thuộc dòng son bóng mới của Black Rouge, mang đến những phong cách biến hóa hiện đại, dễ dùng, rất dễ diện hàng ngày cho đôi môi căng mọng, quyến rũ.', null, 2, 8, 15);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Tint Bóng Black Rouge Half N Half Water Glow HG08', N'son-black-rouge-half-n-half-water-glow.png', 220000, 6, 1, N'Son tint bóng siêu lì có màu Black Rouge Half N Half Water Glow HG08 có màu đỏ nâu quyến rũ mang đến lớp son căng mọng nhẹ nhàng trên môi ngay tức thì, trong suốt và ẩm mịn giúp màu son lên môi chuẩn và tươi mát, căng mọng tràn đầy sức sống.', null, 2, 8, 7);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Black rouge Cotton Lip Color Choco Red T01', N'son-black-rouge.jpg', 195000, 10, 4, N'Black rouge Cotton Lip Color Choco Red T01 là màu nâu đỏ chính hiệu nhưng thiên tone đỏ nhiều hơn. Điều này giúp Choco Red phù hợp với mọi sắc tố da và mọi dễ sử dụng hằng ngày.', null, 2, 8, 7);
INSERT INTO lipstick_store.dbo.product (product_name, product_image, product_price, product_quantity, product_sold, product_short_description, product_description, category_id, brand_id, color_id) VALUES (N'Son Black rouge Cotton Lip Color Tomato Box T04', N'son-black-rouge-cotton-lip-tomato.jpg', 195000, 10, 4, N'Black rouge Cotton Lip Color Tomato Box T04 là màu đỏ gạch, hay còn gọi là đỏ san hô. Khi thoa Tomato box kiểu lòng môi, bạn sẽ thấy son ánh hồng nhiều hơn. Nếu thoa full môi, bạn sẽ thấy một lớp nền đỏ trầm và thiên cam nhẹ.', null, 2, 8, 15);
